    scanf("%lf", &x);
    
    // Aproximação
    double coeff = 1.0;
    double power = 1.0;
    double result = coeff;
    
    for (int n = 2; n <= 20; n = n + 2) {
        coeff = - coeff / (n * (n - 1));
        power = power * x * x;
        result += coeff * power;
    }
    
    // Imprime resultado
    printf("f(%.4lf) = %.4lf\n", x, result);
}

